/**
   Create a map of the vertex ai search urls (very long) to a short url with a unique id for each url.
  Ensures each original URL gets a consistent shortened form while maintaining uniqueness.
 * @param {Array<Object>} chunks - An array of chunk objects, each containing:
 *   - {Object} web - The web object containing:
 *     - {string} uri - The URI associated with the chunk.
 *     - {string} title - The title of the chunk (not used in the resolution process).
 * @returns {Record<string, {url: string, idx: number}>} - A map where the key is the URL
 *          and the value is an object containing the URL and its original index in the array.
 */
function resolve_urls(chunks) {
  if(!chunks || chunks.length===0){
    chunks=[]
  }
  const prefix = "https://vertexaisearch.cloud.google.com/id/"
  // Extract the 'uri' from each chunk's 'web' object
  const urls = chunks?.map((chunk) => chunk?.web?.uri);

  // Initialize an empty object to store the resolved URLs
  /**
   * @type {Record<string, {url: string, idx: number}>}
   */
  const resolvedMap = {};

  // Populate the resolvedMap with the URLs and their corresponding index
  for (let i = 0; i < urls.length; i++) {
    const url = urls[i];
    resolvedMap[url] = `${prefix}${$('attach index as id').item.json.id}-${i}`;
  }

  // Return the map of resolved URLs
  return resolvedMap;
}


/**
 * Extracts and formats citation information from a response object.
 *
 * This function processes the grounding metadata from a response object and constructs a list of citation
 * objects. Each citation includes the start and end indices of the text segment, and a list of related
 * segments with links to external resources.
 *
 * @param {Object} response - The response object containing candidate and grounding metadata.
 * @param {Record<string, {url: string, idx: number}>} resolvedUrlsMap - A map of resolved URLs for each grounding chunk URI.
 * @returns {Array<Object>} citations - A list of citation objects, each containing:
 *   - {number} startIndex - The starting index of the cited segment.
 *   - {number} endIndex - The ending index of the cited segment.
 *   - {Array<Object>} segments - A list of citation segments, each containing:
 *     - {string} label - The title of the citation.
 *     - {string} shortUrl - The resolved URL for the citation.
 *     - {string} value - The original URI for the citation.
 */
function get_citations(response, resolvedUrlsMap) {

  // Initialize an empty array to store citation objects.
  /**
   * @type {Array<Object>}
   */
  const citations = [];

  // Check if response and candidates exist, if not return an empty citations array.
  if (!response || !response.candidates) {
    console.log("no candidates in response");
    return citations;
  }
  const candidate = response?.candidates[0];

  // Ensure that grounding metadata and grounding supports exist in the candidate.
  if (
    !candidate.groundingMetadata ||
    !candidate.groundingMetadata.groundingSupports
  ) {
    console.log("no groundingMetadata in candidate");
    return citations;
  }

  // Iterate over each grounding support in the candidate's grounding metadata.
  for (const support of candidate.groundingMetadata.groundingSupports) {
    const citation = {};

    // Skip if there's no segment information.
    if (!support.segment) continue;

    // Set the start index, defaulting to 0 if not provided.
    const startIndex =
      support.segment.startIndex !== null ? support.segment.startIndex : 0;

    // Ensure that endIndex is provided; skip if missing.
    if (support.segment.endIndex === null) continue;

    // Set the citation's startIndex and endIndex.
    citation.startIndex = startIndex;
    citation.endIndex = support.segment.endIndex;

    citation.segments = [];

    // If grounding chunk indices exist, process each chunk.
    if (
      support.groundingChunkIndices &&
      support.groundingChunkIndices.length > 0
    ) {
      for (const ind of support.groundingChunkIndices) {
        try {
          const chunk = candidate.groundingMetadata.groundingChunks[ind];
          const resolvedUrl = resolvedUrlsMap[chunk.web.uri] || null;

          // Push the segment details (title, resolved URL, original URI) to the segments array.
          citation.segments.push({
            label: chunk.web.title.split(".").slice(0, -1).join("."),
            shortUrl: resolvedUrl,
            value: chunk.web.uri,
          });
        } catch (error) {
          // If an error occurs (e.g., index out of bounds or missing fields), skip the chunk.
          continue;
        }
      }
    }

    // Log the citation object (for debugging purposes).
    // console.log("citation", citation);

    // Push the processed citation to the citations array.
    citations.push(citation);
  }

  // Return the citations array inside an object.
  return citations;
}

/**
 * Inserts citation markers into a text string based on start and end indices.
 *
 * This function takes the original text and a list of citations, where each citation contains
 * the start and end indices, as well as the corresponding citation segments. It inserts
 * citation markers at the specified positions in the text.
 *
 * @param {string} text - The original text string.
 * @param {Array<Object>} citationsList - A list of citation objects, each containing:
 *   - {number} startIndex - The starting index of the citation in the original text.
 *   - {number} endIndex - The ending index of the citation in the original text.
 *   - {Array<Object>} segments - An array of objects where each object represents a citation segment containing:
 *     - {string} label - The label for the citation (typically the title).
 *     - {string} shortUrl - The URL to link to for the citation.
 *
 * @returns {string} - The modified text with citation markers inserted.
 */
function insert_citation_markers(text, citationsList) {
  // Sort the citations first by 'endIndex' in descending order, and then by 'startIndex' in descending order
  // This ensures that insertions at the end of the string don't affect earlier parts of the text
  const sortedCitations = citationsList.sort((a, b) => {
    if (a.endIndex === b.endIndex) {
      return b.startIndex - a.startIndex; // Secondary sorting by 'startIndex'
    }
    return b.endIndex - a.endIndex; // Primary sorting by 'endIndex'
  });

  let modifiedText = text;

  // Iterate over each sorted citation and insert the corresponding citation markers
  for (const citationInfo of sortedCitations) {
    const endIdx = citationInfo.endIndex;
    let markerToInsert = "";

    // Create a citation marker for each segment in the citation
    for (const segment of citationInfo.segments) {
      markerToInsert += ` [${segment.label}](${segment.shortUrl})`;
    }

    // Insert the citation marker at the specified 'endIdx' position in the text
    modifiedText =
      modifiedText.slice(0, endIdx) +
      markerToInsert +
      modifiedText.slice(endIdx);
  }

  return modifiedText;
}

function dedupeByShortUrl(array) {
  const seen = new Set();
  return array.filter(item => {
    if (!seen.has(item.shortUrl)) {
      seen.add(item.shortUrl);
      return true;
    }
    return false;
  });
}

